import { Component } from '@angular/core';

import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  products = products;

  share(link: string) {
    window.alert('The product has been shared!');
    window.open(`https://t.me/share/url?url=${link}`);
  }
  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
}

/*
Copyright Google LLC. All Rights Reserved.
Use of this source code is governed by an MIT-style license that
can be found in the LICENSE file at https://angular.io/license
*/
<h2>Products</h2>

<div *ngFor="let product of products">
  <h3>
    <a
      [title]="product.name + ' details'"
      [routerLink]="['/products', product.id]"
    >
      <!-- With *ngFor, the <div> repeats for each product in the list. -->
      {{ product.name }}
    </a>
  </h3>
  <div class="product-list-img">
    <img src="{{ product.image1 }}" alt="img" class=".img" />
    <img src="{{ product.image2 }}" alt="img" class=".img" />
    <img src="{{ product.image3 }}" alt="img" class=".img" />
  </div>

  <p *ngIf="product.description">
    <!-- use an *ngIf directive so that Angular only creates the <p> element if the current product has a description. -->
    Description: {{ product.description }}
  </p>

  <button type="button" (click)="share(product.link)">Share</button>

  <app-product-alerts [product]="product" (notify)="onNotify()">
  </app-product-alerts>
</div>
